function pieHover(e,o,r){r&&(percent=parseFloat(r.series.percent).toFixed(2),$("#hover").html('<span style="font-weight: bold; color: '+r.series.color+'">'+r.series.label+" ("+percent+"%)</span>"))}function pieClick(e,o,r){r&&(percent=parseFloat(r.series.percent).toFixed(2),alert(""+r.series.label+": "+percent+"%"))}$(function(){for(var e=[],o=Math.floor(10*Math.random())+1,r=0;o>r;r++)e[r]={label:"Series"+(r+1),data:Math.floor(100*Math.random())+1};$.plot($("#pie"),e,{series:{pie:{show:!0,label:{show:!1,formatter:function(e,o){return'<div style="font-size:11px;text-align:center;padding:4px;color:white;">'+e+"<br/>"+Math.round(o.percent)+"%</div>"},threshold:.1}}},legend:{show:!0,noColumns:1,labelFormatter:null,labelBoxBorderColor:"#000",container:null,position:"ne",margin:[5,10],backgroundColor:"#24272b",backgroundOpacity:.8},grid:{hoverable:!0,clickable:!0}}),$("#interactive").bind("plothover",pieHover),$("#interactive").bind("plotclick",pieClick)});